mdq:check.code:
library(metadig)
if (pubDatePresent) {
  # A publication date is present, so now check if it is in
  # the future
  metadataDate <- as.Date(datasetPubDate)
  currentDate <- Sys.Date()
  if(metadataDate > currentDate) {
    failure("The publication date '%s' is in the future.")
  } else {
    success("A publication date is present and is not in the future.")
  }
} else {
  failure("A publication date is not present.") 
}

